    //     addMessage(message);
    //     socket.emit("send-message", message, room);

    //     setInputMsg("");
    // }
    function submitUser(user) {

    }
    const sendData = async (e) => {
        const roomref = db.collection('rooms').doc('GofTpXT2CG49N8vmOH4x').collection(inputRoom);
        const snapshot = await roomref.get();
        if(username!=="" && inputRoom!=="" && snapshot){
            console.log("true");
        }
    }
    function joinRoom(room,username) {
        socket.emit("join-room", room, () => {
        //   addMessage(`You connected to room: ${room}`);
        });
        
        setInputRoom("");
    }


    useEffect(() => {
        socket.on("connect", () => {
          //Reciveing messages from server
          addMessage(`You connected with id: ${socket.id}`);
    
          socket.on("recieve-message", (message) => {
            addMessage(message);
          });
        });
    
        return () => {
          // remove event listner
          socket.off("connect");
        };
      }, []);
      
  return (
    <div>
        <h1>Home</h1>
        <input
          type="text"
          placeholder="Enter username"
          onChange={(e) => setUsername(e.target.value)}
        ></input>
        <input
          type="text"
          placeholder="Room ID..."
          id="room-input"
          onChange={(e) => setInputRoom(e.target.value)}
        ></input>
        <button
          style={{ width: "70px", height: "30px" }}
          type="button"
          id="room-button"
          onClick={() => joinRoom(inputRoom,username)}
        >
          Join
        </button>
        
        {username}
    </div>
  )
}

export default Home
