import React from 'react'
import { useState, useEffect } from "react";
import { db } from "../firebase-config";
import {
  collection,
  getDocs,
  addDoc,
  deleteDoc,
  doc,
} from "firebase/firestore";

const Signup = () => {
    
  const [newUsername, setNewusername] = useState("");
  const [newEmail, setNewemail] = useState("");
  const [newPassword, setNewpassword] = useState("");
  
  const [userlist, setUserlist] = useState([]);
  const userlistCollectionRef = collection(db, "users");

    const createUserlist = async () => {
        if (newUsername!=""){
          await addDoc(userlistCollectionRef, { username: newUsername, email: newEmail, password: newPassword });
        }
        window.location.reload(false);
      };

      useEffect(() => {
        const getUserlist = async () => {
          const data = await getDocs(userlistCollectionRef);
          setUserlist(data.docs.map((doc) => ({ ...doc.data(), id: doc.id })));
        };
    
        getUserlist();
      }, []);

      useEffect(() => {
        const getTodolist = async () => {
          const data = await getDocs(userlistCollectionRef);
          setUserlist(data.docs.map((doc) => ({ ...doc.data(), id: doc.id })));
        };
    
        getTodolist();
      }, []);   

  return (
    <div>
        <form action="">
            <input type="text" placeholder='Enter username' onChange={(event) => {setNewusername(event.target.value);}}/>
            <input type="text" placeholder='Enter email' onChange={(event) => {setNewemail(event.target.value);}}/>
            <input type="text" placeholder='Enter password' onChange={(event) => {setNewpassword(event.target.value);}}/>
            <button onClick={createUserlist}>Submit</button>
        </form>

        <div>

        </div>
    </div>
  )
}

export default Signup
