import React, {useEffect} from "react";
import { io } from "socket.io-client";

const socket = io("http://localhost:3001")

const form = document.getElementById("form");
const chatBox = document.getElementById("chat-box");
const messageInput = document.getElementById("message-input");
const button = document.getElementById("message-button");

if(form){
  form.addEventListener("submit", e => {
  e.preventDefault();
  const message = messageInput.value;
  if (message === "") return 
  displayMessage(message);

  messageInput.value = "";

  })
}




function displayMessage(message) {
  const div = document.createElement("div")
  div.textContent = message
  document.getElementById("chat-box").append(div)
}

const Home = () => {
  useEffect(() => {
    socket.on("connect", () => {
      //Reciveing messages from server
      socket.on("welcome", (data) => {
        console.log("msg from server", data);
        displayMessage(data);
      })

      //send a message to server
      socket.emit("msg", "Thanks for connecting Home")
    })

    return () => {
      // remove event listner
      // socket.off("connect");
    };
  }, []);

  
  return (
    <>
    <div id="chat-box">Home</div>
    <form id="form">
      <input type="text" placeholder="say something" id="message-input"></input>
      <button type="submit" id="message-button" style="width:10px; height:10"></button>
      <button type="button" id="room-button"></button>
    </form>
    </>
  )
}

export default Home
