  //     })

  //     //send a message to server
  //     socket.emit("msg", "Thanks for connecting Home")
  //   })

  //   return () => {
  //     // remove event listner
  //     // socket.off("connect");
  //   };
  // }, []);

  
  return (
    <>
    <div id="chat-box">Home</div>
    <form id="form">
      <input type="text" placeholder="say something" id="message-input"></input>
      <button style={{width:'70px', height:'30px'}} type="submit" id="message-button" >Send</button>
      <input type="text" placeholder="Room ID..." id="room-input"></input>
      <button style={{width:'70px', height:'30px'}} type="button" id="room-button">Join</button>
    </form>
    </>
  )
}

export default Home
