import React from 'react'
import { useState, useEffect } from "react";
import { db } from "../firebase-config";
import {
  collection,
  getDocs,
  addDoc,
  deleteDoc,
  doc,
  and,
  query, 
  where,
} from "firebase/firestore";
import bcrypt from 'bcryptjs';


const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  
  const [userlist, setUserlist] = useState([]);
  const userlistCollectionRef = collection(db, "users");
  
  
    const userLogin = async (e) => {
        e.preventDefault();
        if ( email!="" && password!=""){
          const q = query(userlistCollectionRef, where("email", "==", email));
          const qdocs = await getDocs(q);
          let pword;
          {qdocs.map((pdoc) => ({pword: pdoc.password}))
        
          }

          alert(pword.toString());
          const saltRounds = 10; // Number of salt rounds for hashing
          // const pword = qdocs.docs.password
          // alert(pword.length());

          const hashedPassword = bcrypt.hashSync(password,saltRounds);
          
          alert(hashedPassword);
          if (hashedPassword == pword){
            alert('Password verified');
          }
          else{
            alert('Invalid Password');
          }
        
          // await addDoc(userlistCollectionRef, { email: email, password: hashedPassword });
        }
        else{
          alert("Please enter a valid username, email and password");
        }
        window.location.reload(false);
      };

      useEffect(() => {
        const getUserlist = async () => {
          const data = await getDocs(userlistCollectionRef);
          setUserlist(data.docs.map((doc) => ({ ...doc.data(), id: doc.id })));
        };
    
        getUserlist();
      }, []);

        

  return (
    <div>
        <div className="form">  
          <form onSubmit={userLogin}>
            <input type="email" placeholder='Enter email' onChange={(event) => {setEmail(event.target.value);}}/>
            <input type="password" placeholder='Enter password' onChange={(event) => {setPassword(event.target.value);}}/>
            <button>Submit</button>
          </form>
        </div>
        <div>
        {userlist.map((user)=>{
            return(
                <div className="flex justify-between">
                    {" "}
                    <div>
                        <p>{user.password}</p>
                        <p>{user.email}</p>
                    </div>
                </div>
            )
        })}
        </div>
    </div>
  )
}

export default Login
