import React, {useEffect} from "react";
import { io } from "socket.io-client";

const socket = io("http://localhost:3001")

const form = document.getElementById("form");
const chatBox = document.getElementById("chat-box");
const messageInput = document.getElementById("message-input");
const sendButton = document.getElementById("message-button");
const roomInput = document.getElementById("room-input");
const roomButton = document.getElementById("room-button");

if(form){
  form.addEventListener("submit", e => {
  e.preventDefault();
  const message = messageInput.value;
  const room = roomInput.value;
  if (message === "") return 
  displayMessage(message);

  messageInput.value = "";

  });
}

if(roomButton){
  roomButton.addEventListener("click", e => {

  });
}


function displayMessage(message) {
  const div = document.createElement("div")
  div.textContent = message
  document.getElementById("chat-box").append(div)
}

const Home = () => {
  useEffect(() => {
    socket.on("connect", () => {
      //Reciveing messages from server
      socket.on("welcome", (data) => {
        console.log("msg from server", data);
        displayMessage(data);
      })

      //send a message to server
      socket.emit("msg", "Thanks for connecting Home")
    })

    return () => {
      // remove event listner
      // socket.off("connect");
    };
  }, []);

  
  return (
    <>
    <div id="chat-box">Home</div>
    <form id="form">
      <input type="text" placeholder="say something" id="message-input"></input>
      <button style={{width:'70px', height:'30px'}} type="submit" id="message-button" >Send</button>
      <input type="text" placeholder="Room ID..." id="room-input"></input>
      <button style={{width:'70px', height:'30px'}} type="button" id="room-button">Join</button>
    </form>
    </>
  )
}

export default Home
